import { createSlice } from '@reduxjs/toolkit'

const initialState = {
  items: [],
  filter: '',
}

export const contactsSlice = createSlice({
  name: 'contacts',
  initialState,
  reducers: {
    addContact: (state, { payload }) => {
      const checkName = state.items
        .map((contact) => contact.name)
        .includes(payload.name)
      const checkNumber = state.items
        .map((contact) => contact.number)
        .includes(payload.number)

      if (checkName) {
        alert(`${payload.name} is already in contacts`)
      } else if (checkNumber) {
        alert(`Number ${payload.number} is already in contacts`)
      } else return [...state.items, payload]
    },
    deleteContact: (state, { payload }) =>
      state.filter.filter(({ id }) => id !== payload),
    changeFilter: (state, { payload }) => (state.filter = payload),
  },
})

console.log(contactsSlice)

export const { addContact, deleteContact, changeFilter } = contactsSlice.actions
export const contactsReducer = contactsSlice.reducer
