import { createSlice } from '@reduxjs/toolkit'

const initialState = {
  items: [],
  filter: '',
}

const itemsSlice = createSlice({
  name: 'items',
  initialState,
  reducers: {
    addContact: (state, action) => {
      const checkName = state.items
        .map((contact) => contact.name)
        .includes(actions.payload.name)
      const checkNumber = state.items
        .map((contact) => contact.number)
        .includes(actions.payload.number)

      if (checkName) {
        alert(`${actions.payload.name} is already in contacts`)
      } else if (checkNumber) {
        alert(`Number ${actions.payload.number} is already in contacts`)
      } else [...state, actions.payload]
    },
    deleteContact: (state, action) =>
      state.filter.filter(({ id }) => id !== action.payload),
    changeFilter: (state, action) => (state.filter = action.payload),
  },
})

export const { addContact, deleteContact, changeFilter } = itemsSlice.actions
export default itemsSlice.reducer
